import React from 'react'
import Flag from '../image/Ellipse 12.png';
import './DidYouKnow.css';
import Divider from '../image/SwirlDivider.png';


function DidYouKnow() {
  return (
    <div>
      <div className="didyou">
      <div className="didyouimage">
        <img className='didyouimg' src={Flag} alt="Did you know"/>
      </div>
      <div className="codrops-header">
        <h1>Did You Know</h1>
        <img src={Divider} alt="Caption Divider" style={{ width: '150px', height: '25px', marginLeft: '0px', backgroundColor:'transparent' }} />

        <p>Varanasi is one of the world's oldest continually inhabited cities. Kashi, its ancient name, was associated with a kingdom of the same name of 2,500 years ago. The Buddha is recorded in the Pali canon to have given his first sermon, "The Setting in Motion of the Wheel of Dharma", at nearby Sarnath in 528 BCE.
</p>
      </div>
    </div>
    </div>
  )
}

export default DidYouKnow
