            </Navbar.Brand> */}
            <Navbar.Brand href="#">
              <img className="App-logo" src={ICLogo} style={{ marginTop: '0px', backgroundColor:'transparent' }} alt="IC Logo" />
            </Navbar.Brand>
          
            <div class="container">
    
    <div class="row cf">
      <div class="three col">
        <div class="hamburger" id="hamburger-1">
          <span class="line"></span>
          <span class="line"></span>
          <span class="line"></span>
        </div>
      </div>
     
  </div>
           
</div>
            

              
              
            
          </Container>


        </Navbar>

      ))}
      <div style={{ width: '100%', flex: 1, height: '8px', backgroundColor: '#8b2266 ', margin: '-11px', marginTop: '-17px', marginLeft: '0px' }} />

    </>

  );


}


